mapper.update:

	update blog set blogTitle = #{blogTitle}


				,
				logoFile = #{logoFile}


	where id = #{id}

mapper.select:
select orderdata.rn, orderdata.logoFile, orderdata.id, orderdata.blogTitle, orderdata.userName, orderdata.joinDate from (select rownum rn, data.logoFile, data.id, data.blogTitle, data.userName, data.joinDate from (select b.logoFile, b.id, b.blogTitle, userName, TO_CHAR(joinDate,'YYYY/MM/DD') joinDate from blog b, users u where b.id = u.id
and blogTitle like '%'||#{keyword}||'%'
and userName like '%'||#{keyword}||'%'
) data) orderdata where rn>= #{begin} and rn <= #{end}
